MediaCard.propTypes = {
    classes: PropTypes.object.isRequired,
};

class ProjectsList extends Component {


    componentDidMount() {
        this.getProjects();
    }

    getProjects = () => {
        const action = { type: 'GET_PROJECTS' };
        this.props.dispatch(action);
    }

    displayDescription() {
        if (!this.props.project.description) {
        return null;
    } else{
        return (this.props.project.description)
    }
}

    displaythumbnail() {
            if(!this.props.project.thumbnail) {
            return null;
            } else {
                return (this.props.project.thumbnail)
            }
    }

    displayWebsite() {
        if (!this.props.project.website) {
            return null;
        } else {
            return (this.props.project.website)
        }
    }

    displayProjectDate() {
        if (!this.props.project.date_completed) {
            return null;
        } else {
            return (this.props.project.date_completed)
        }

    }

    displayTagId(){
        if (!this.props.project.tag_id) {
            return null;
        }

    }



    render() {
        const styles = {
            card: {
                maxWidth: 345,
            },
            media: {
                height: 140,
            },
        };       
        return (
            function MediaCard(props) {
                const { classes } = props;
                return (
                    <Card className={classes.card}>
                        <CardActionArea>
                            <CardMedia
                                className={classes.media}
                                image="/static/images/cards/contemplative-reptile.jpg"
                                title="Contemplative Reptile"
                            />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                        {this.props.project.name}
                                </Typography>
                                <Typography component="p">

                        <br></br>
                          {this.displayDescription()}
                <br></br>
                        {this.displaythumbnail()}
                <br></br>
                          {this.displayWebsite()}
                <br></br>
                {this.displayProjectDate()}
                <br></br>
                          {this.displayTagId()}
                 </Typography>
        </CardContent>
      </CardActionArea>
                    <CardActions>
                        <Button size="small" color="primary">
                            Share
                    </Button>
                        <Button size="small" color="primary">
                            Learn More
                    </Button>
                    </CardActions>
    </Card >
        );
    }
        )}
}

ImgMediaCard.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ImgMediaCard);

